--
-- Cấu trúc bảng cho bảng `tblarticle_category`
--

CREATE TABLE `tblarticle_category` (
  `id` int(10) NOT NULL,
  `category` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `alias` varchar(200) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `parent_id` int(10) NOT NULL,
  `home` tinyint(1) NOT NULL,
  `menu` tinyint(1) NOT NULL,
  `footer` tinyint(1) NOT NULL,
  `page_sub` tinyint(1) NOT NULL,
  `heading` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `meta_title` varchar(100) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `meta_des` varchar(300) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `keyword` varchar(300) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `ordernum` int(10) NOT NULL,
  `status` tinyint(1) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
