--

CREATE TABLE `tblquestions` (
  `id` int(10) NOT NULL,
  `question` tinytext DEFAULT NULL,
  `reply` text DEFAULT NULL,
  `ordernum` int(11) DEFAULT 0,
  `status` tinyint(1) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Đang đổ dữ liệu cho bảng `tblquestions`
--

INSERT INTO `tblquestions` (`id`, `question`, `reply`, `ordernum`, `status`) VALUES
(1, 'Tôi chỉ mới có ý tưởng sơ bộ, tôi có được tư vấn không?', '<p>\r\n	Mona Media c&oacute; những chuy&ecirc;n gia trong website v&agrave; phần mềm, ch&uacute;ng t&ocirc;i hiểu về những g&igrave; bạn cần nhiều hơn cả bản th&acirc;n bạn n&ecirc;n đừng ngần ngại, h&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i ngay cả khi bạn chưa l&agrave;m r&otilde; được y&ecirc;u cầu tại:</p>\r\n', 0, 1),
(2, 'Các bước thanh toán khi thiết kế website', '<p>\r\n	Ch&uacute;ng t&ocirc;i &aacute;p dụng h&igrave;nh thức thanh to&aacute;n 50% đăt cọc sau khi k&iacute; hợp đồng v&agrave; 50% thanh to&aacute;n khi thanh l&yacute; hợp đồng. Đối với những dự &aacute;n c&oacute; gi&aacute; trị lớn hơn 200.000.000vnđ th&igrave; số lần thanh to&aacute;n chia l&agrave;m 3: 50% 30% 20%</p>\r\n', 0, 1),
(3, 'Tôi có nhận được hợp đồng pháp lý và giấy tờ không?', '<p>\r\n	Mona Media l&agrave; một c&ocirc;ng ty chuy&ecirc;n nghiệp n&ecirc;n c&aacute;c bạn c&oacute; thể y&ecirc;n t&acirc;m về c&aacute;c thủ tục ho&agrave;n chỉnh như hợp đồng, bi&ecirc;n bản nghiệm thu, t&agrave;i liệu hướng dẫn, ho&aacute; đơn dịch vụ&hellip; đều được đẩm bảo đầy đủ</p>\r\n', 0, 1),
(4, 'Website có load nhanh không, nhanh cỡ nào?', '<p>\r\n	Mona Media lu&ocirc;n đảm bảo sử dụng những kỹ thuật lập tr&igrave;nh ph&ugrave; hợp nhất cho y&ecirc;u cầu của website, dự tr&ugrave; trước những t&igrave;nh huống ph&aacute;t sinh v&agrave; tối ưu ho&aacute; tốc độ load trang hết mức. Dự kiến tốc độ v&agrave;o khoản 1s 1 lần load</p>\r\n', 0, 1),
(5, 'Tôi có thể tự thay đổi hình, nội dung, số điện thoại... được không', '<p>\r\n	Tất cả mọi content/images tr&ecirc;n website bạn đều c&oacute; thể tuỳ &yacute; thay đổi theo &yacute; th&iacute;ch m&agrave; kh&ocirc;ng bị hạn chế trong bất k&igrave; phần n&agrave;o.</p>\r\n', 0, 1),
(6, 'Mona thiết kế độc quyền hay dựa vào mẫu nước ngoài?', '<p>\r\n	Mỗi website do Mona Media l&agrave;m ra đều được thiết kế v&agrave; lập tr&igrave;nh ri&ecirc;ng cho mỗi kh&aacute;ch h&agrave;ng, ch&uacute;ng t&ocirc;i cam kết kh&ocirc;ng sử dụng lại thiết kế với bất k&igrave; kh&aacute;ch h&agrave;ng n&agrave;o v&agrave; cam kết kh&ocirc;ng sao ch&eacute;p/copy thiết kế từ b&ecirc;n kh&aacute;c</p>\r\n', 0, 1),
(7, 'Website của tôi có hiển thị đẹp trên di động không?', '<p>\r\n	Website của bạn sẽ hiển thị ho&agrave;n hảo tr&ecirc;n c&aacute;c thiết bị di động v&agrave; hơn nữa: ch&uacute;ng tối cam kết hiển thị ho&agrave;n hảo tr&ecirc;n mọi thiết bị c&oacute; m&agrave;n h&igrave;nh: Smartphone, Tablet, m&aacute;y chiếu, m&agrave;n h&igrave;nh laptop, m&agrave;n h&igrave;nh desktop, m&agrave;n h&igrave;nh tivi</p>\r\n', 0, 1),
(8, 'Website của tôi có chuẩn SEO chứ', '<p>\r\n	Website của bạn sẽ ho&agrave;n to&agrave;n đầy đủ những yếu tố kĩ thuật để th&acirc;n thiện với c&ocirc;ng cụ t&igrave;m kiếm nhất</p>\r\n', 0, 1),
(9, 'Tôi sẽ được bàn giao source code chứ?', '<p>\r\n	Hơn cả thế, Qu&yacute; kh&aacute;ch sẽ nhận được Source Code, file thiết kế gốc, file HTML gốc v&agrave; tất cả những th&ocirc;ng tin kỹ thuật kh&aacute;c từ Mona Media. Ch&uacute;ng t&ocirc;i sẽ b&agrave;n giao mọi thứ m&agrave; bạn cần.</p>\r\n', 0, 1);

