) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='Cung cấp giải pháp';

--
-- Đang đổ dữ liệu cho bảng `tblsolutions`
--

INSERT INTO `tblsolutions` (`id`, `title`, `description`, `ordernum`, `status`) VALUES
(4, 'CÔNG NGHỆ BẢO MẬT ĐA TẦNG', '<p>\r\n	học trực tuyến cần được trang bị những phương thức bảo mật hiện đại v&agrave; chuy&ecirc;n nghiệp, Ch&uacute;ng t&ocirc;i l&agrave; một chuy&ecirc;n gia trong lĩnh vực bảo mật website. Ngay từ đầu ch&uacute;ng t&ocirc;i đ&atilde; t&iacute;nh to&aacute;n để website bạn bảo mật nhất c&oacute; thể th&ocirc;ng qua c&aacute;c hệ thống v&agrave; c&aacute;ch viết Code.</p>\r\n', 0, 1),
(5, 'HIỂN THỊ TỐT TRÊN MỌI THIẾT BỊ DI ĐỘNG', '<p>\r\n	Xu hướng kh&aacute;ch h&agrave;ng sử dụng di động ng&agrave;y c&agrave;ng nhiều v&agrave; website học trực tuyến lu&ocirc;n được kh&aacute;ch h&agrave;ng tận dụng để học trong thời gian di chuyển v&igrave; vậy đ&acirc;y l&agrave; t&iacute;nh năng cần được tối ưu ngay từ đầu. Ch&uacute;ng t&ocirc;i hiểu được điều đ&oacute; n&ecirc;n giao diện website của bạn sẽ được hiển thị tốt tr&ecirc;n mọi thiết bị di động như điện thoại v&agrave; m&aacute;y t&iacute;nh bảng.</p>\r\n', 0, 1),
(6, 'GIAO DIỆN ĐƯỢC THIẾT KẾ RIÊNG VÀ ĐỘC QUYỀN', '<p>\r\n	Ch&uacute;ng t&ocirc;i biết được mỗi trung t&acirc;m l&agrave; một style kh&aacute;c nhau v&igrave; vậy website cũng vậy ch&uacute;ng t&ocirc;i kh&ocirc;ng thể sử dụng chung một giao diện cho nhiều website kh&aacute;c nhau, Tại Mona mỗi dự &aacute;n đều được ch&uacute;ng t&ocirc;i thiết kế giao diện ri&ecirc;ng v&agrave; độc quyền của ri&ecirc;ng kh&aacute;ch h&agrave;ng, gi&uacute;p họ c&oacute; được một trang web với style duy nhất</p>\r\n', 0, 1),
(7, 'SỬ DỤNG CÔNG NGHỆ VÀ NGÔN NGỮ MẠNH MẼ', '<p>\r\n	Đối với dự &aacute;n kh&oacute; như thiết kế web dạy học trực tuyến th&igrave; việc lựa chọn ng&ocirc;n ngữ lập tr&igrave;nh cần được c&acirc;n nhắc, đối với Mona Media ch&uacute;ng t&ocirc;i lựa chon .NET để thực hiện dự &aacute;n n&agrave;y. V&igrave; đ&acirc;y l&agrave; ng&ocirc;n ngữ mạnh mẽ với t&iacute;nh bảo mật cao, được sử dụng rộng r&atilde;i trong c&aacute;c dự &aacute;n web-application.</p>\r\n', 0, 1);

